     collapsed : true,
     displayInfo : true,
     displayAdvancedSearch : true,
     placeholder : "Rechercher un lieu, une adresse",
     displayMarker : true,
     markerStyle : L.icon(iconUrl : 'https://maps.google.com/mapfiles/kml/pushpin/ylw-pushpin.png');
     zoomTo : 15,
